    }
  };
  return(
    <Fragment>
    <h1>Posts</h1><br/>
    <div className="posts">
    {isAuthenticated &&(

    <div className="post-form">

        <form className="form my-1">
          <textarea
            name="text"
            cols="30"
            rows="5"
            placeholder="Create a post"
            value ={name}
            onChange={e => onChange(e)}
            required
          ></textarea>
          <input type="submit" onSubmit ={e => onSubmit(e)} className="btn btn-dark my-1" value="Submit" />
        </form>
      </div>
    )}

      {posts === null ? (<p>Damn!</p>):(
        posts.map(post => (<div key={post._id}>

          <div className="post bg-white p-1 my-1">
       <div>
         <a href="profile.html">
           <img
             className="round-img"
             src= {user.avatar}
             alt=""
           />
           <h4>{user.name}</h4>
         </a>
       </div>
       <div>
         <p className="my-1">
           {post.text}
         </p>
          <p className="post-date">
             Posted on {post.date}
         </p>
         </div>
         </div>
          </div>)
      ))}

    </div>
    </Fragment>
  );
}

Posts.propTypes = {
  posts: PropTypes.array.isRequired,
  isAuthenticated: PropTypes.bool
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps)(Posts);
